; 0 address offsets in the form 0(a0) are optimized automatically by the assembler
; the value 0 will turn this feature off. This is good for producing a byte-perfect copy of
; the original ROM. When hacking, you don't need this so you can set it to 1 if you want
zeroOffsetOptimization = 0

	cpu 68000
	include "ps2.macrosetup.asm"


	include "ps2.constants.asm"
	include "ps2.macros.asm"


StartOfRom:
	if (*) <> 0
		fatal	"Start of ROM is at address $\{*}, but it should be at address 0."
	endif

VectorTable:
	dc.l	system_stack&$FFFFFF, EntryPoint, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	HBlank, ErrorTrap, VBlank, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap
	dc.l	ErrorTrap, ErrorTrap, ErrorTrap, ErrorTrap

Header:
	dc.b	"SEGA MEGA DRIVE " ; Console name
	dc.b 	"(C)SEGA 1990.JAN" ; Copyright/Date
	dc.b 	"PHANTASY STAR 2                                 " ; Domestic name
	dc.b 	"PHANTASY STAR 2                                 " ; International name
	dc.b 	"GM 00005501-02"   ; Version

Checksum:
	dc.w	$3792		; Checksum
	dc.b 	"J               " ; I/O Support
	dc.l 	StartOfRom		; ROM Start

ROMEndLoc:
	dc.l	EndOfRom-1		; ROM End
	dc.l 	ram_start&$FFFFFF		; RAM Start
	dc.l 	$FFFFFF		; RAM End
	dc.l 	$5241F820		; Backup RAM ID
	dc.l 	$200001		; Backup RAM start address
	dc.l 	$203FFF		; Backup RAM end address
	dc.b 	"            "	; Modem support
	dc.b 	"                                        "	; Notes
	dc.b 	"UE              " ; Country
EndOfHeader:
	if (*) <> $200
		fatal	"End of header is at address $\{*}, but it should be at address $200. The header + vector table must be exactly 512 bytes long"
	endif

ErrorTrap:
	nop
	nop
	nop

EntryPoint:
	move.b	(hw_version).l, d0	; get hardware version
	andi.b	#$F, d0		; stored in the lower nibble
	beq.s	SkipSecurity	; branch if hardware is older than Genesis III
	move.l	#'SEGA', (security_addr).l	; satisfy the TMSS

SkipSecurity:
	btst	#6, (hw_expansion_control).l
	beq.s	ChecksumTest
	cmpi.l	#'init', (checksum_four_cc).w
	beq.w	GameInit	; branch if checksum routine has already run

ChecksumTest:
	movea.l	#EndOfHeader, a0		; start checking bytes after the header ($200)
	movea.l	#ROMEndLoc, a1		; stop at the end of ROM
	move.l	(a1), d0
	moveq	#0, d1

ChecksumLoop:
	add.w	(a0)+, d1
	cmp.l	a0, d0
	bcc.s	ChecksumLoop
	movea.l	#Checksum, a1	; read the checksum
	cmp.w	(a1), d1			; compare correct checksum to the one in ROM
	bne.w	ChecksumError   ; if they don't match, branch

; Checksum good
	lea	($FFFFFE00).w, a6
	moveq	#0, d7

	move.w	#$7F, d6
-
	move.l	d7, (a6)+
	dbf	d6, -

	move.b	(hw_version).l, d0
	andi.b	#$C0, d0				; get video type (NTSC, PAL)
	move.b	d0, ($FFFFFFF8).w	; and store them
	move.l	#'init', (checksum_four_cc).w	; Checksum routine successful
GameInit:
	lea	(ram_start&$FFFFFF).l, a6	; load ram

	moveq	#0, d7
	move.w	#$3F7F, d6
-
	move.l	d7, (a6)+
	dbf	d6, -

	bsr.w	VDPSetupGame
	bsr.w	LoadPCMDrums
	bsr.w	JoypadInit
	move.b	#GameModeID_Sega, (game_mode_index).w	; start from Sega screen


MainGameLoop:
	move.b	(game_mode_index).w, d0	; get screen id
	andi.w	#$1C, d0
	lsl.w	#1, d0
	jsr	GameModeTable(pc,d0.w)
	bra.s	MainGameLoop

; ===================================================
; Table for every screen in the game
; ===================================================
GameModeTable:

PtrGameMode_Sega:	    jmp	(GameMode_Sega).l
	nop
PtrGameMode_Title:    jmp	(GameMode_Title).l
	nop
PtrGameMode_Ending:   jmp	(GameMode_Ending).l
	nop
PtrGameMode_Map:    jmp	(GameMode_Map).l
	nop
PtrGameMode_Building: jmp	(GameMode_Building).l
	nop
PtrGameMode_Battle:   jmp	(GameMode_Battle).l
	nop
PtrGameMode_Intro:    jmp	(GameMode_Intro).l
	nop
; ===================================================

ChecksumError:
	bsr.w	VDPSetupGame
	move.l	#$C0000000, (vdp_control_port).l	; write to CRAM

	moveq	#$3F, d7
-
	move.w	#$E, (vdp_data_port).l
	dbf	d7, -


ChecksumFailedLoop:
	bra.s	ChecksumFailedLoop

RunObjects:
	lea	(object_ram).w, a0

	move.w	#$3F, d7
-
	bsr.s	RunObject
	adda.w	#$40, a0		; process next object
	dbf	d7, -

	rts

RunObject:
	move.w	(a0), d0
	beq.w	RunObjectEnd	; return if there is no object at this address
	lea	(ObjectTable-4).l, a1
	lsl.w	#2, d0
	jsr	(a1,d0.w)

	btst	#0, 2(a0)
	bne.w	loc_3DC
	bclr	#7, 2(a0)
	btst	#3, 2(a0)
	beq.w	loc_3E4
	move.l	$14(a0), d0
	add.l	d0, $A(a0)
	move.l	$18(a0), d0
	add.l	d0, $E(a0)
	move.w	$1C(a0), d0
	move.w	$12(a0), d1
	add.w	d0, d1
	cmpi.w	#$400, d1
	bcs.w	loc_3DC
	cmpi.w	#$4000, d1
	bcc.w	loc_3DC
	move.w	d1, $12(a0)
	move.w	$A(a0), d0
	move.w	d0, $20(a0)
	move.w	$E(a0), d0
	subi.w	#$90, d0
	muls.w	#$400, d0
	divs.w	$12(a0), d0
	neg.w	d0
	addi.w	#$D0, d0
	cmpi.w	#$80, d0
	bls.w	RunObjectEnd
	bmi.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, $1E(a0)
	btst	#1, 2(a0)
	bne.s	RunObjectEnd
	lea	($FFFFF000).w, a1
	moveq	#0, d0
	move.b	$12(a0), d0
	asl.w	#4, d0
	adda.l	d0, a1
-
	cmpi.w	#$E, (a1)
	bcs.s	+
	adda.w	#$10, a1
	bra.s	-

+
	addq.w	#2, (a1)
	moveq	#0, d1
	move.w	(a1), d1
	adda.l	d1, a1
	move.w	a0, (a1)
	bset	#7, 2(a0)

RunObjectEnd:
	rts

loc_3DC:
	movea.l	a0, a1
	moveq	#0, d1
	bra.w	loc_6D38

loc_3E4:
	move.w	$A(a0), d0
	add.w	$14(a0), d0
	bmi.s	+
	btst	#4, 2(a0)
	bne.s	++
	sub.w	($FFFFF722).w, d0
	bcc.s	++
+
	add.w	($FFFFF722).w, d0

+
	move.w	d0, $A(a0)
	move.w	$E(a0), d0
	add.w	$18(a0), d0
	bmi.s	+
	btst	#4, 2(a0)
	bne.s	++
	sub.w	($FFFFF720).w, d0
	bcc.s	++
+
	add.w	($FFFFF720).w, d0

+
	move.w	d0, $E(a0)
	move.w	$A(a0), d0
	btst	#4, 2(a0)
	bne.s	++
	sub.w	($FFFFF71E).w, d0
	bcc.s	+
	add.w	($FFFFF722).w, d0
+
	addi.w	#$80, d0

+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	cmpi.w	#$1E0, d0
	bcc.w	RunObjectEnd
	move.w	d0, $20(a0)
	move.w	$E(a0), d0
	btst	#4, 2(a0)
	bne.s	++
	sub.w	($FFFFF71C).w, d0
	bcc.s	+
	add.w	($FFFFF720).w, d0
+
	addi.w	#$80, d0

+
	cmpi.w	#$40, d0
	bls.w	RunObjectEnd
	bmi.w	RunObjectEnd
	cmpi.w	#$180, d0
	bcc.w	RunObjectEnd
	move.w	d0, $1E(a0)
	btst	#1, 2(a0)
	bne.s	+++	; rts
	lea	($FFFFF000).w, a1
	moveq	#0, d0
	btst	#5, 2(a0)
	bne.s	+
	move.w	$1E(a0), d0
	subi.w	#$80, d0
	not.w	d0
	andi.w	#$FC, d0
	asl.w	#2, d0
	adda.l	d0, a1
/
	cmpi.w	#$E, (a1)
	bcs.s	+
	adda.w	#$10, a1
	bra.s	-
+
	addq.w	#2, (a1)
	moveq	#0, d1
	move.w	(a1), d1
	adda.l	d1, a1
	move.w	a0, (a1)
	bset	#7, 2(a0)
+
	rts

BuildSprites:
	move.b	#0, (sprite_link_field_count).w
	move.b	#$50, (sprite_count).w		; sprite limit = 80
	lea	(sprite_table).w, a1
	move.l	a1, ($FFFFF608).w
	lea	($FFFFF000).w, a6

	moveq	#$3F, d7
loc_4E4:
	move.w	(a6), d0
	beq.s	loc_512
	moveq	#2, d6
loc_4EA:
	movea.l	a6, a5
	adda.w	d6, a5			; add 2 to address in order to get the pointer of the object
	movea.w	(a5), a0		; load pointer of object
	movea.l	4(a0), a1
	move.w	$24(a0), d0		; Sprite Mappings index
	lsl.w	#1, d0
	adda.w	(a1,d0.w), a1
	tst.b	(a1)
	beq.w	loc_572
	bmi.w	loc_572
	bsr.w	loc_526

loc_50C:
	addq.w	#2, d6		; load next object
	subq.w	#2, (a6)	; decrement object count
	bne.s	loc_4EA

loc_512:
	addq.w	#8, a6
	addq.w	#8, a6
	dbf	d7, loc_4E4

	movea.l	($FFFFF608).w, a0		; load sprite table
	move.l	#0, (a0)
	rts

loc_526:
	movea.l	($FFFFF608).w, a2		; load sprite table address
	moveq	#0, d1
	move.b	(a1)+, d1
	subq.b	#1, d1		; get number of attributes for each mapping
	move.w	$1E(a0), d2
	move.w	$20(a0), d3

FillSpriteAttributesLoop:
	tst.b	(sprite_count).w
	beq.s	loc_56C		; branch if there are already 80 sprites
	subq.b	#1, (sprite_count).w	; otherwise subtract 1 and build this sprite

	move.b	(a1)+, d0
	ext.w	d0
	add.w	d2, d0
	move.w	d0, (a2)+		; set Y position
	move.b	(a1)+, (a2)+	; set sprite size
	addq.b	#1, (sprite_link_field_count).w
	move.b	(sprite_link_field_count).w, (a2)+		; set link field
	move.b	(a1)+, d0
	lsl.w	#8, d0
	move.b	(a1)+, d0
	add.w	8(a0), d0
	move.w	d0, (a2)+		; set art tiles and flags
	move.b	(a1)+, d0
	ext.w	d0
	add.w	d3, d0
	move.w	d0, (a2)+		; set X position
	dbf	d1, FillSpriteAttributesLoop

loc_56C:
	move.l	a2, ($FFFFF608).w
	rts

loc_572:
	move.w	(a1)+, d0
	move.w	(a1)+, d1
	add.w	$20(a0), d0
	subi.w	#$80, d0
	add.w	$1E(a0), d1
	subi.w	#$80, d1
	andi.w	#$FFF8, d0
	asr.w	#2, d0
	andi.w	#$FFF8, d1
	asl.w	#4, d1
	add.w	d0, d1
	move.l	#$FF6000, d2
	add.w	d1, d2
	move.b	(a1)+, d3
	ext.w	d3
	move.b	(a1)+, d4
	ext.w	d4
-
	movea.l	d2, a5
	move.w	d3, d5
-
	move.w	(a1)+, d0
	beq.s	+
	add.w	8(a0), d0
+
	move.w	d0, (a5)+
	dbf	d5, -

	addi.w	#$80, d2
	dbf	d4, --

	bra.w	loc_50C

