	dc.b	$24, $24, $24, $24, $24, $20, $1C, $1C
	dc.b	$18, $18, $14, $14, $14, $10, $10, $10
	dc.b	$10, $0C, $0C, $0C, $0C, $0C, $08, $08
	dc.b	$08, $08, $08, $08, $04, $04, $04, $04
