CalculateHitRate:
	moveq	#0, d0
	move.w	$18(a3), d0		; get character's dexterity
	lsl.w	#8, d0			; move it to higher byte -- should be lsl.l to get the full register otherwise the value gets truncated and considers only the lower 2 bytes
	divu.w	$14(a1), d0		; divide it by the enemy's agility
	move.w	#$CC, d2 	; 80% hit rate
	cmpi.w	#$80, d0
	bcs.s	+			; branch if lower than 128
	move.w	#$E5, d2	; 90% hit rate
	cmpi.w	#$180, d0
	bcs.s	+			; branch if lower than 384
	move.w	#$FF, d2	; 100% hit rate (Yay!)
+
	_btst	#4, 0(a3)	; if it's set, halve whole hit rate value --  don't know which enemy does this, probably Dark Force
	beq.s	loc_2790
	lsr.w	#1, d2		; halve value
loc_2790:
	tst.w	d2
	beq.s	+		; branch if hit rate is 0 -- always fails
	bsr.w	UpdateRNGSeed
	andi.w	#$FF, d0
	cmp.w	d0, d2
	bcc.s	++		; return if successful (d2 higher than RNG)
+
	bset	#4, 3(a2)
	moveq	#-1, d0		; attack failed
+
	rts

loc_27AA:
	tst.w	(enemy_data_buffer+$18).w	;  is escape rate 0 (boss battles)?
	bne.s	+							; if not, branch
	cmpi.w	#$1F4, d6				; otherwise techniques with this attack rate value (500) always fail (Vol, Brose, etc)
	beq.s	loc_27C0
+
	move.b	($FFFFF73A).w, d1	; get technique type, specifically if it works for either biomonster or a robot
	and.b	1(a1), d1		; check if bit is set in RAM
	bne.s	loc_27C8		; if it's set, branch and process technique normally
loc_27C0:
	move.w	#$1204, (battle_script_id).w	; display "Didn't work" text
	moveq	#0, d2	; make attack fail
loc_27C8:
	_btst	#3, 0(a3)
	beq.s	loc_2790
	lsr.w	#1, d2
	bra.s	loc_2790

CalculateAttackDamage:
	bsr.w	UpdateRNGSeed
	andi.l	#$1F, d0	; random value from 0 to 31
	addi.w	#$54, d0	; add 84 to it
	add.w	$1A(a3), d0	; add the attack value for character
	lsl.l	#8, d0		; multiply it by 256
	move.w	$1E(a1), d1	; get enemy's defense
	mulu.w	#5, d1		; multiply it by 5
	addi.w	#$64, d1	; add 100 to it
	divu.w	d1, d0		; divide character's attack by the enemy's defense
	move.w	d3, d1		; index for weapon used
	andi.w	#$7F, d1
	lsl.w	#4, d1		; get weapon currently used
	lea	(InventoryData+$E).l, a4		; get attack value
	adda.w	d1, a4
	moveq	#0, d1
	move.b	(a4), d1	; get weapon attack value
	addq.w	#2, d1		; add 2 to it
	mulu.w	d1, d0		; multiply character's attack value by this number
	lsr.w	#8, d0		; divide total attack value by 256
CheckEnemyAlive:
	sub.w	d0, 2(a1)			; subtract damage from enemy's current HP
	bhi.s	+		; return if enemy is still alive
	move.w	#0, 2(a1)			; force enemy's HP to 0 (enemy dead)
	bset	#5, 3(a2)
	moveq	#0, d1
	move.w	$C(a1), d1			; get experience points of enemy which was killed
	add.l	d1, (enemy_data_buffer+$30).w	; add them to the total
	move.w	$A(a1), d1		; get meseta value
	add.l	d1, (enemy_data_buffer+$34).w	; add it to the total
+
	rts

CalculateTechniqueDamage:
	bsr.w	UpdateRNGSeed
	andi.l	#$1F, d0	; random value from 0 to 31
	addi.w	#$54, d0	; add 84 to it
	mulu.w	d6, d0		; multiply this by the technique attack rate
	divu.w	#$64, d0	; divide the total by 100
	bra.s	CheckEnemyAlive


