	neg.b	d0
	sub.w	d0, d1		; Frequency = Frequency - Slide Speed
	move.w	d1, d0
	andi.w	#$3FF, d0
	cmpi.w	#$26C, d0
	bcc.w	+
	subi.w	#$59B, d1	; Frequency got	< 26C -	make it	4D1 in lower octave
+
	move.w	d1, $C(a3)
	rts

